"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Scan, CheckCircle, XCircle, RefreshCcw } from "lucide-react";
import { cn } from "@/lib/utils";

export default function AccessControl() {
    const [scanStatus, setScanStatus] = useState<"idle" | "scanning" | "valid" | "invalid">("idle");
    const [scannedData, setScannedData] = useState<any>(null);

    const simulateScan = () => {
        setScanStatus("scanning");
        setTimeout(() => {
            // Randomly succeed or fail for demo purposes
            const isValid = Math.random() > 0.3;

            if (isValid) {
                setScanStatus("valid");
                setScannedData({
                    name: "Jean-Michel Kotto",
                    id: "EMP-2076-8842",
                    tier: "Gold Member",
                    access: "Tribune Présidentielle"
                });
            } else {
                setScanStatus("invalid");
                setScannedData({
                    reason: "Billet déjà utilisé ou inexistant."
                });
            }
        }, 1500);
    };

    const reset = () => {
        setScanStatus("idle");
        setScannedData(null);
    };

    return (
        <div className="container py-12 flex flex-col items-center justify-center min-h-[calc(100vh-theme(spacing.16))]">
            <div className="text-center space-y-2 mb-8">
                <h1 className="text-2xl font-bold">Empire Guard - Point de Contrôle</h1>
                <p className="text-muted-foreground">Scanner de contrôle d'accès (Lot 7 & 11)</p>
            </div>

            <Card className="w-full max-w-md border-2 shadow-2xl">
                <CardHeader className="text-center">
                    <CardTitle>Scanner NFC / QR</CardTitle>
                    <CardDescription>Approchez le terminal ou le billet.</CardDescription>
                </CardHeader>
                <CardContent className="flex flex-col items-center space-y-8 py-8">

                    {/* Visual Indicator */}
                    <div className={cn(
                        "w-48 h-48 rounded-full flex items-center justify-center transition-all duration-500",
                        scanStatus === "idle" && "bg-muted text-muted-foreground",
                        scanStatus === "scanning" && "bg-yellow-100 text-yellow-600 animate-pulse border-4 border-yellow-400",
                        scanStatus === "valid" && "bg-green-100 text-green-600 border-4 border-green-500 scale-110",
                        scanStatus === "invalid" && "bg-red-100 text-red-600 border-4 border-red-500 shake"
                    )}>
                        {scanStatus === "idle" && <Scan className="h-24 w-24" />}
                        {scanStatus === "scanning" && <Scan className="h-24 w-24 animate-spin-slow" />}
                        {scanStatus === "valid" && <CheckCircle className="h-24 w-24" />}
                        {scanStatus === "invalid" && <XCircle className="h-24 w-24" />}
                    </div>

                    {/* Status Message */}
                    <div className="text-center space-y-1 h-20">
                        {scanStatus === "idle" && <p className="text-xl font-medium">Prêt à scanner</p>}
                        {scanStatus === "scanning" && <p className="text-xl font-medium">Vérification...</p>}
                        {scanStatus === "valid" && (
                            <>
                                <p className="text-2xl font-bold text-green-600">ACCÈS AUTORISÉ</p>
                                <p className="text-sm font-medium">{scannedData.name} - {scannedData.access}</p>
                            </>
                        )}
                        {scanStatus === "invalid" && (
                            <>
                                <p className="text-2xl font-bold text-red-600">ACCÈS REFUSÉ</p>
                                <p className="text-sm font-medium">{scannedData.reason}</p>
                            </>
                        )}
                    </div>

                    {/* Controls */}
                    <div className="w-full space-y-3">
                        {scanStatus === "idle" ? (
                            <Button size="lg" className="w-full text-lg h-12" onClick={simulateScan}>
                                Scanner (Simulation)
                            </Button>
                        ) : (
                            <Button size="lg" variant="outline" className="w-full text-lg h-12" onClick={reset}>
                                <RefreshCcw className="mr-2 h-5 w-5" /> Nouveau Scan
                            </Button>
                        )}
                    </div>

                </CardContent>
            </Card>
        </div>
    );
}

"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Save, Plus, Trash2, Trophy } from "lucide-react";

// Mock Data Types
type Team = {
    id: string;
    name: string;
    points: number;
    played: number;
    wins: number;
    draws: number;
    losses: number;
};

export default function LeagueAdmin() {
    const [teams, setTeams] = useState<Team[]>([
        { id: "1", name: "Akwa United", points: 36, played: 14, wins: 11, draws: 3, losses: 0 },
        { id: "2", name: "Bonapriso Stars", points: 34, played: 14, wins: 10, draws: 4, losses: 0 },
        { id: "3", name: "Deido Boyz", points: 28, played: 14, wins: 9, draws: 1, losses: 4 },
        { id: "4", name: "New-Bell Warriors", points: 22, played: 14, wins: 6, draws: 4, losses: 4 },
    ]);

    const [matchInput, setMatchInput] = useState({
        homeTeam: "",
        awayTeam: "",
        homeScore: "",
        awayScore: "",
    });

    const handleUpdateScore = () => {
        // Simulation of score update logic
        alert(`Score enregistré: ${matchInput.homeTeam} ${matchInput.homeScore} - ${matchInput.awayScore} ${matchInput.awayTeam}\nLe classement serait mis à jour automatiquement.`);
    };

    return (
        <div className="container py-8 space-y-8">
            <div className="flex items-center justify-between">
                <div>
                    <h1 className="text-3xl font-bold tracking-tight">Administration Ligue</h1>
                    <p className="text-muted-foreground">Lot 12: Gestion des compétitions et résultats.</p>
                </div>
                <Button variant="outline">Retour au Dashboard</Button>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                {/* Match Result Input */}
                <Card>
                    <CardHeader>
                        <CardTitle>Saisie des Résultats</CardTitle>
                        <CardDescription>Mise à jour en temps réel de l'Empire League.</CardDescription>
                    </CardHeader>
                    <CardContent className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Équipe Domicile</label>
                                <Input
                                    placeholder="Ex: Akwa United"
                                    value={matchInput.homeTeam}
                                    onChange={(e) => setMatchInput({ ...matchInput, homeTeam: e.target.value })}
                                />
                            </div>
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Équipe Extérieur</label>
                                <Input
                                    placeholder="Ex: Bonapriso Stars"
                                    value={matchInput.awayTeam}
                                    onChange={(e) => setMatchInput({ ...matchInput, awayTeam: e.target.value })}
                                />
                            </div>
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Score Domicile</label>
                                <Input
                                    type="number"
                                    placeholder="0"
                                    value={matchInput.homeScore}
                                    onChange={(e) => setMatchInput({ ...matchInput, homeScore: e.target.value })}
                                />
                            </div>
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Score Extérieur</label>
                                <Input
                                    type="number"
                                    placeholder="0"
                                    value={matchInput.awayScore}
                                    onChange={(e) => setMatchInput({ ...matchInput, awayScore: e.target.value })}
                                />
                            </div>
                        </div>
                        <Button className="w-full" onClick={handleUpdateScore}>
                            <Save className="mr-2 h-4 w-4" /> Enregistrer le match
                        </Button>
                    </CardContent>
                </Card>

                {/* Live Standings Preview */}
                <Card>
                    <CardHeader>
                        <CardTitle>Classement Actuel (Aperçu)</CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="rounded-md border">
                            <table className="w-full text-sm">
                                <thead className="bg-muted/50 border-b">
                                    <tr className="text-left">
                                        <th className="p-3 font-medium">Equipe</th>
                                        <th className="p-3 font-medium text-center">Pts</th>
                                        <th className="p-3 font-medium text-center">J</th>
                                        <th className="p-3 font-medium text-right">Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {teams.map((team) => (
                                        <tr key={team.id} className="border-b last:border-0 hover:bg-muted/50">
                                            <td className="p-3 font-medium">{team.name}</td>
                                            <td className="p-3 text-center font-bold">{team.points}</td>
                                            <td className="p-3 text-center text-muted-foreground">{team.played}</td>
                                            <td className="p-3 text-right">
                                                <Button variant="ghost" size="icon" className="h-8 w-8 text-destructive">
                                                    <Trash2 className="h-4 w-4" />
                                                </Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                        <Button variant="outline" className="w-full mt-4">
                            <Plus className="mr-2 h-4 w-4" /> Ajouter une équipe
                        </Button>
                    </CardContent>
                </Card>
            </div>
        </div>
    );
}

import * as React from "react"
import { cn } from "@/lib/utils"

export interface InputProps
    extends React.InputHTMLAttributes<HTMLInputElement> { }

const Input = React.forwardRef<HTMLInputElement, InputProps>(
    ({ className, type, ...props }, ref) => {
        return (
            <input
                type={type}
                className={cn(
                    "flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50",
                    className
                )}
                ref={ref}
                {...props}
            />
        )
    }
)
Input.displayName = "Input"

export { Input }
